import React from 'react'
import styled from 'styled-components'
import '../App.css';


const QuizFood = () => {

const [VegIsShown, setVegIsShown] = useState(false);
const [PescIsShown, setPescIsShown] = useState(false);
const [MeatIsShown, setMeatIsShown] = useState(false);

const handleVegClick = event => {
  setVegIsShown(current => !current);
};

const handlePescClick = event => {
  setVegIsShown(current => !current);
};

const handleMeatClick = event => {
  setVegIsShown(current => !current);
};

const MeatOption = () => {
  return (
    <h1>meat</h1>
  )
}

const PescOption = () => {
  return (
    <h1>Pesc</h1>
  )
}

const VegOption = () => {
  return (
      <div>
          <h2>What products do you eat in an average week?</h2>
          <form>
              <button>Cheese</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>

              <button>Eggs</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>

              <button>Milk</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>
          </form>
      </div>
  )
}

const PescOptions = () => {
  return (
    <form>
        <label>What products do you eat?</label>
        <button>Milk</button>
        <button>Cheese</button>
    </form>
  )
}

const MeatOptions = () => {
  return (
    <form>
        <label>What products do you eat?</label>
        <button>Milk</button>
        <button>Cheese</button>
    </form>
  )
}

  return (
      <form>
        <h2>How would you describe your eating habits?</h2>
            <button>Vegan</button>
            <button onClick={handleVegClick}>Vegetarian</button>
            <button onClick={handlePescClick}>Pescatarian</button>
            <button onClick={handleMeatClick}>Meat Eater</button>
            {VegIsShown && <VegOptions/>}
            {PescIsShown && <PescOptions/>}
            {MeatIsShown && <MeatOptions/>}
      </form>
  )
}

export default QuizFood;
