import React, {useState} from 'react'
// import styled from 'styled-components'
import '../App.css';


const QuizFood = () => {

const [VegIsShown, setVegIsShown] = useState(false);
const [PescIsShown, setPescIsShown] = useState(false);
const [MeatIsShown, setMeatIsShown] = useState(false);

let number = 0 

// const handleVegClick = (event) => {
//   event.preventDefault();
//   setVegIsShown(!VegIsShown);
// };

const handlePescClick = () => {
  setPescIsShown(current => !current);
};

const handleMeatClick = () => {
  setMeatIsShown(current => !current);
};

function MeatOption () {
  return (
    <h1>meat</h1>
  )
}

function PescOption(){
  return (
    <h1>Pesc</h1>
  )
}

function VegOption () {
  return (
      <div>
          <h2>What products do you eat in an average week?</h2>
          <form>
              <button>Cheese</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>

              <button>Eggs</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>

              <button>Milk</button>
              <div>
                  <p>How many portions a week?</p>
                  <p>0</p>
                  <button>+</button>
                  <button>-</button>
              </div>
          </form>
      </div>
  )
}

  return (
      <form>
        <h2>How would you describe your eating habits?</h2>
            <button>Vegan</button>
            <button onClick={(event) => {
              event.preventDefault();
              setVegIsShown(!VegIsShown)}}>
              {`${VegIsShown ? 'Vegetarian' : 'Vegetarian'}`}
            </button>
            <button onClick={handlePescClick}>Pescatarian</button>
            <button onClick={handleMeatClick}>Meat Eater</button>
            {VegIsShown && <VegOption/>}
            {PescIsShown && <PescOption/>}
            {MeatIsShown && <MeatOption/>}
      </form>
  )
}

export default QuizFood;
